var time = $('#cowndown_timmer').attr('data-time');
var countDownDate = new Date(time).getTime();
var x = setInterval(function() {
    var now = new Date().getTime();
    var distance = countDownDate - now;
    var days = Math.floor(distance / (1000 * 60 * 60 * 24));
    var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
    var seconds = Math.floor((distance % (1000 * 60)) / 1000);

    if (distance < 0) {
        var days = 0;
        var hours = 0;
        var minutes = 0;
        var seconds = 0;
    }
    var html = '';
    html += '<span class="countdown-row countdown-show3">';
    html += '<span class="countdown-section">';
    html += '<span class="countdown-amount">'+  days +'</span>';
    html += '<span class="countdown-period">Days</span>';
    html += '</span>';
    html += '<span class="countdown-section">';
    html += '<span class="countdown-amount">'+  hours +'</span>';
    html += '<span class="countdown-period">Hours</span>';
    html += '</span>';
    html += '<span class="countdown-section">';
    html += '<span class="countdown-amount">'+  minutes +'</span>';
    html += '<span class="countdown-period">Minutes</span>';
    html += '</span>';
    html += '<span class="countdown-section">';
    html += '<span class="countdown-amount">'+  seconds +'</span>';
    html += '<span class="countdown-period">Seconds</span>';
    html += '</span>';
    document.getElementById('cowndown_timmer').innerHTML = html;

}, 1000);
